import React,{Component} from 'react';
import './App.css';
import 'tachyons';

class App extends Component {
  constructor(){
    super()
    this.state = {
      joke: ''
    }
  }
  componentDidMount(){
    const fetchUsers = async function(){
      const user = await fetch('https://api.chucknorris.io/jokes/random');
      const data = await user.json();
      var value1 = data.value;
      value1 = value1.toLowerCase();
      value1 = value1.replace("chuck","Rajni");
      value1 = value1.replace("norris","Kant");
      document.getElementById('demo').innerHTML = value1;
    }
     fetchUsers();
  }
  refreshPage = () => {
    window.location.reload(false);
  }
  render(){
  return (
    <div className="tc">
    <div className="container">
      <div className='tc bg-light-green ttc dib br3 pa3 ma2 grow bw shadow-5'>
      <p id='demo'></p>
      <a class="f6 grow no-underline br-pill ba ph3 pv2 mb2 dib dark-gray" href="#0" onClick={window.location.reload}>Refresh</a>
      </div>
    </div>
    </div>
  );
}
}

export default App;
